import React, { useState } from 'react';
import { Form, Button, Alert, InputGroup } from 'react-bootstrap';
import './RegistrationForm.css';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import logo from './logo.png';
import { FormProvider } from "react-hook-form";

function Header() {
  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
      <div className="container-fluid">
        <a className="navbar-brand" href="#">
          <img src={logo} alt="Logo" width="200" height="100" className="d-inline-block align-text-top me-2" />
        </a>
      </div>
    </nav>
  );
}

function DateOfBirth(props) {
  const { register, errors } = useFormContext();
  return (
    <div className="form-group">
      <label htmlFor="dob">Date of Birth</label>
      <div className="row">
        <div className="col">
          <select
            name="day"
            className={classNames("form-control", { "is-invalid": errors.day })}
            ref={register({ required: true })}
            defaultValue=""
          >
            <option value="" disabled>Day</option>
            {days.map((day) => (
              <option key={day} value={day}>
                {day}
              </option>
            ))}
          </select>
          {errors.day && (
            <div className="invalid-feedback">Please select a day</div>
          )}
        </div>
        <div className="col">
          <select
            name="month"
            className={classNames("form-control", { "is-invalid": errors.month })}
            ref={register({ required: true })}
            defaultValue=""
          >
            <option value="" disabled>Month</option>
            {months.map((month) => (
              <option key={month} value={month}>
                {month}
              </option>
            ))}
          </select>
          {errors.month && (
            <div className="invalid-feedback">Please select a month</div>
          )}
        </div>
        <div className="col">
          <select
            name="year"
            className={classNames("form-control", { "is-invalid": errors.year })}
            ref={register({ required: true })}
            defaultValue=""
          >
            <option value="" disabled>Year</option>
            {years.map((year) => (
              <option key={year} value={year}>
                {year}
              </option>
            ))}
          </select>
          {errors.year && (
            <div className="invalid-feedback">Please select a year</div>
          )}
        </div>
      </div>
    </div>
  );
}

const RegistrationForm = () => {
  // State variables for form fields
  const [fullName, setFullName] = useState('');
  const [contactNumber, setContactNumber] = useState('');
  const [email, setEmail] = useState('');
  const [day, setDay] = useState('');
  const [month, setMonth] = useState('');
  const [year, setYear] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  // State variables for form validation and API response
  const [validated, setValidated] = useState(false);
  const [alertVariant, setAlertVariant] = useState('');
  const [alertMessage, setAlertMessage] = useState('');

  // Event handlers for form fields
  const handleFullNameChange = (event) => setFullName(event.target.value);
  const handleContactNumberChange = (event) => {
    const value = event.target.value.replace(/[^\d]/g, '');
    let formattedValue = '';

    if (value.length > 0) {
      formattedValue = value.slice(0, 3);

      if (value.length >= 4) {
        formattedValue += '-' + value.slice(3, 6);
      }

      if (value.length >= 7) {
        formattedValue += '-' + value.slice(6, 10);
      }
    }

    setContactNumber(formattedValue);
  };
  const handleEmailChange = (event) => setEmail(event.target.value);
  const handleDayChange = (event) => setDay(event.target.value);
  const handleMonthChange = (event) => setMonth(event.target.value);
  const handleYearChange = (event) => setYear(event.target.value);
  const handlePasswordChange = (event) => setPassword(event.target.value);
  const handleConfirmPasswordChange = (event) =>
    setConfirmPassword(event.target.value);

  // Form submission handler
  const handleSubmit = (event) => {
    event.preventDefault();
    const form = event.currentTarget;

    if (form.checkValidity() === false) {
      event.stopPropagation();
    } else {
      // Send API request to create new user account and display toast message
      const requestOptions = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          full_name: fullName,
          contact_number: contactNumber,
          email: email,
          date_of_birth: `${day} ${month} ${year}`,
          password: password,
        }),
      };

      fetch('https://fullstack-test-navy.vercel.app/api/users/create', requestOptions)
        .then((response) => response.json())
        .then((data) => {
          // Display alert message based on API response
          if (data.success) {
            setAlertVariant('success');
            setAlertMessage(data.message);
          } else {
            setAlertVariant('danger');
            setAlertMessage(data.message);
          }
        })
        .catch((error) => {
          setAlertVariant('danger');
          setAlertMessage('An error occurred while processing your request.');
        });
    }

    setValidated(true);
  };

  // Cancel form submission handler
  const handleCancel = () => {
    setFullName('');
    setContactNumber('');
    setEmail('');
    setDay('');
    setMonth('');
    setYear('');
    setPassword('');
    setConfirmPassword('');
    setValidated(false);
    setAlertVariant('');
    setAlertMessage('');
  };

const days = [];
for (let i = 1; i <= 31; i++) {
  days.push(i);
}

const months = [  'Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun',  'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec',];

const currentYear = new Date().getFullYear();
const years = [];
for (let i = currentYear; i >= currentYear - 100; i--) {
  years.push(i);
}

  return (
    <div>
      <Header />
      <div className="form-header">
          <h3>Create User Account</h3>
        </div>
    <div className='RegistrationForm'>

      <Form noValidate validated={validated} onSubmit={handleSubmit}>
        <Form.Group controlId='fullName'>
          <Form.Label>Full Name</Form.Label>
          <Form.Control
            required
            type='text'
            placeholder='Enter full name'
            value={fullName}
            onChange={handleFullNameChange}
          />
          <Form.Control.Feedback type='invalid'>
            Please enter your full name.
          </Form.Control.Feedback>
        </Form.Group>
        <Form.Group controlId='email'>
      <Form.Label>Email</Form.Label>
      <Form.Control
        required
        type='email'
        placeholder='Enter email'
        value={email}
        onChange={handleEmailChange}
      />
      <Form.Control.Feedback type='invalid'>
        Please enter a valid email address.
      </Form.Control.Feedback>
    </Form.Group>
    <Form.Group controlId='contactNumber'>
  <Form.Label>Contact Number</Form.Label>
  <InputGroup>

    <Form.Control
      required
      type='tel'
      placeholder='555-555-5555'
      pattern='[0-9]{3}-[0-9]{3}-[0-9]{4}'
      value={contactNumber}
      onChange={handleContactNumberChange}
    />
    <Form.Control.Feedback type='invalid'>
      Please enter a valid Canadian phone number.
    </Form.Control.Feedback>
  </InputGroup>
</Form.Group>
<Form.Group controlId='dateOfBirth'>
  <Form.Label>Date of Birth</Form.Label>
  <div className='form-row'>
    <div className='col-md-4'>
      <Form.Control
        required
        as='select'
        value={day}
        onChange={handleDayChange}
      >
        <option value=''>Day</option>
        {days.map((day) => (
          <option key={day} value={day}>
            {day}
          </option>
        ))}
      </Form.Control>
      <Form.Control.Feedback type='invalid'>
        Please select a valid day.
      </Form.Control.Feedback>
    </div>
    <div className='col-md-4'>
      <Form.Control
        required
        as='select'
        value={month}
        onChange={handleMonthChange}
      >
        <option value=''>Month</option>
        {months.map((month, index) => (
          <option key={index} value={month}>
            {month}
          </option>
        ))}
      </Form.Control>
      <Form.Control.Feedback type='invalid'>
        Please select a valid month.
      </Form.Control.Feedback>
    </div>
    <div className='col-md-4'>
      <Form.Control
        required
        as='select'
        value={year}
        onChange={handleYearChange}
      >
        <option value=''>Year</option>
        {years.map((year) => (
          <option key={year} value={year}>
            {year}
          </option>
        ))}
      </Form.Control>
      <Form.Control.Feedback type='invalid'>
        Please select a valid year.
      </Form.Control.Feedback>
    </div>
  </div>
</Form.Group>

    <Form.Group controlId='password'>
      <Form.Label>Password</Form.Label>
      <Form.Control
        required
        type='password'
        placeholder='Enter password'
        value={password}
        onChange={handlePasswordChange}
        pattern='^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[a-zA-Z]).{8,}$'
      />
      <Form.Control.Feedback type='invalid'>
        Password must be at least 8 characters and contain at least one uppercase letter, one lowercase letter, and one number.
      </Form.Control.Feedback>
    </Form.Group>
    <Form.Group controlId='confirmPassword'>
      <Form.Label>Confirm Password</Form.Label>
      <Form.Control
        required
        type='password'
        placeholder='Confirm password'
        value={confirmPassword}
        onChange={handleConfirmPasswordChange}
        pattern={password}
      />
      <Form.Control.Feedback type='invalid'>
        Passwords do not match.
      </Form.Control.Feedback>
    </Form.Group>

  </Form>
  {alertMessage && (
    <Alert className='mt-3' variant={alertVariant}>
      {alertMessage}
    </Alert>
  )}

</div>
<div className="button">
    <Button className="btn-cancel" variant='secondary' type='button' onClick={handleCancel}>
          Cancel
        </Button>
        <Button className="btn-submit" variant='primary' type='submit'onClick={handleSubmit}>
      Submit
    </Button>
        </div>
</div>
);
};

export default RegistrationForm;
