import React, { useState } from 'react';
import { Form, Button, Alert, Col } from 'react-bootstrap';
import './RegistrationForm.css';

const RegistrationForm = () => {
  // State variables for form fields
  const [fullName, setFullName] = useState('');
  const [contactNumber, setContactNumber] = useState('');
  const [email, setEmail] = useState('');
  const [day, setDay] = useState('');
  const [month, setMonth] = useState('');
  const [year, setYear] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  // State variables for form validation and API response
  const [validated, setValidated] = useState(false);
  const [alertVariant, setAlertVariant] = useState('');
  const [alertMessage, setAlertMessage] = useState('');

  // Event handlers for form fields
  const handleFullNameChange = (event) => setFullName(event.target.value);
  const handleContactNumberChange = (event) =>
    setContactNumber(event.target.value);
  const handleEmailChange = (event) => setEmail(event.target.value);
  const handleDayChange = (event) => setDay(event.target.value);
  const handleMonthChange = (event) => setMonth(event.target.value);
  const handleYearChange = (event) => setYear(event.target.value);
  const handlePasswordChange = (event) => setPassword(event.target.value);
  const handleConfirmPasswordChange = (event) =>
    setConfirmPassword(event.target.value);

  // Form submission handler
  const handleSubmit = (event) => {
    event.preventDefault();
    const form = event.currentTarget;

    if (form.checkValidity() === false) {
      event.stopPropagation();
    } else {
      // Send API request to create new user account
      const requestOptions = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          full_name: fullName,
          contact_number: contactNumber,
          email: email,
          date_of_birth: `${day} ${month} ${year}`,
          password: password,
        }),
      };

      fetch('https://fullstack-test-navy.vercel.app/api/users/create', requestOptions)
        .then((response) => response.json())
        .then((data) => {
          // Display alert message based on API response
          if (data.success) {
            setAlertVariant('success');
            setAlertMessage(data.message);
          } else {
            setAlertVariant('danger');
            setAlertMessage(data.message);
          }
        })
        .catch((error) => {
          setAlertVariant('danger');
          setAlertMessage('An error occurred while processing your request.');
        });
    }

    setValidated(true);
  };

  return (
    <div className='RegistrationForm'>
      <h1>Register</h1>
      <Form noValidate validated={validated} onSubmit={handleSubmit}>
        <Form.Group controlId='fullName'>
          <Form.Label>Full Name</Form.Label>
          <Form.Control
            required
            type='text'
            placeholder='Enter full name'
            value={fullName}
            onChange={handleFullNameChange}
          />
          <Form.Control.Feedback type='invalid'>
            Please enter your full name.
          </Form.Control.Feedback>
        </Form.Group>
        <Form.Group controlId='email'>
          <Form.Label>Email</Form.Label>
          <Form.Control
            required
            type='email'
            placeholder='Enter email'
            value={email}
            onChange={handleEmailChange}
          />
          <Form.Control.Feedback type='invalid'>
            Please enter a valid email address.
          </Form.Control.Feedback>
        </Form.Group>
        <Form.Group controlId='dateOfBirth'>
          <Form.Label>Date of Birth</Form.Label>
          <Form.Row>
            <Form.Group controlId='day' as={Col}>
              <Form.Control
                required
                as='select'
                value={day}
                onChange={handleDayChange}
              >
                <option value=''>Day</option>
                {Array.from({ length: 31 }, (_, i) => i + 1).map((day) => (
                  <option key={day} value={day}>
                    {day}
                  </option>
                ))}
              </Form.Control>
              <Form.Control.Feedback type='invalid'>
                Please select a day.
              </Form.Control.Feedback>
            </Form.Group>
            <Form.Group controlId='month' as={Col}>
              <Form.Control
                required
                as='select'
                value={month}
                onChange={handleMonthChange}
              >
                <option value=''>Month</option>
                {[
                  'Jan',
                  'Feb',
                  'Mar',
                  'Apr',
                  'May',
                  'Jun',
                  'Jul',
                  'Aug',
                  'Sep',
                  'Oct',
                  'Nov',
                  'Dec',
                ].map((month) => (
                  <option key={month} value={month}>
                    {month}
                  </option>
                ))}
              </Form.Control>
              <Form.Control.Feedback type='invalid'>
                Please select a month.
              </Form.Control.Feedback>
            </Form.Group>
            <Form.Group controlId='year' as={Col}>
              <Form.Control
                required
                type='number'
                placeholder='Year'
                min={new Date().getFullYear() - 100}
                max={new Date().getFullYear()}
                value={year}
                onChange={handleYearChange}
              />
              <Form.Control.Feedback type='invalid'>
                Please enter a valid year.
              </Form.Control.Feedback>
            </Form.Group>
          </Form.Row>
        </Form.Group>
        <Form.Group controlId='password'>
          <Form.Label>Password</Form.Label>
          <Form.Control
            required
            type='password'
            placeholder='Enter password'
            value={password}
            onChange={handlePasswordChange}
          />
          <Form.Control.Feedback type='invalid'>
            Please enter a valid password.
          </Form.Control.Feedback>
        </Form.Group>
        <Form.Group controlId='confirmPassword'>
          <Form.Label>Confirm Password</Form.Label>
          <Form.Control
            required
            type='password'
            placeholder='Confirm password'
            value={confirmPassword}
            onChange={handleConfirmPasswordChange}
          />
          <Form.Control.Feedback type='invalid'>
            Please confirm your password.
          </Form.Control.Feedback>
        </Form.Group>
        <Button variant='primary' type='submit'>
          Submit
        </Button>
      </Form>
      {alertVariant && alertMessage && (
        <Alert className='mt-3' variant={alertVariant}>
          {alertMessage}
        </Alert>
      )}
    </div>
  );
};

export default RegistrationForm;
