import React, { useState } from 'react';
import { Form, Button, Alert, InputGroup, Spinner } from 'react-bootstrap';
import './RegistrationForm.css';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import logo from './logo.png';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';

function Header() {
  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
      <div className="container-fluid">
        <a className="navbar-brand" href="#">
          <img src={logo} alt="Logo" width="200" height="100" className="d-inline-block align-text-top me-2" />
        </a>
      </div>
    </nav>
  );
}
const days = [];
for (let i = 1; i <= 31; i++) {
  days.push(i);
}

const months = [  'Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun',  'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec',];

const currentYear = new Date().getFullYear();
const years = [];
for (let i = currentYear; i >= currentYear - 100; i--) {
  years.push(i);
}

const handleDisabledSubmit = () => {
  toast.error("There was an error creating the account.", {
    position: "top-right",
    className: 'toast-message',
    hideProgressBar: true
 });
};

const [submitting, setSubmitting] = useState(false);

const RegistrationForm = () => {
  const formik = useFormik({
    initialValues: {
      fullName: '',
      contactNumber: '',
      email: '',
      day: '',
      month: '',
      year: '',
      password: '',
      confirmPassword: '',
    },
    validationSchema: Yup.object({
      fullName: Yup.string()
        .required('Full name is required')
        .matches(/^[a-zA-Z\s]*$/, 'Full name should only contain alphabets and spaces'),
      contactNumber: Yup.string()
        .required('Contact number is required')
        .matches(
          /^(\()?\d{3}(\))?(-|\s)?\d{3}(-|\s)\d{4}$/,
          'Contact number should be in the format 123-456-7890 or (123) 456-7890',
        ),
      email: Yup.string().required('Email is required').email('Invalid email address'),
      day: Yup.string().required('Day is required'),
      month: Yup.string().required('Month is required'),
      year: Yup.string().required('Year is required'),
      password: Yup.string()
        .required('Password is required')
        .matches(
          /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/,
          'Password should be at least 8 characters long and contain at least one uppercase letter, one lowercase letter, one number and one special character',
        ),
      confirmPassword: Yup.string()
        .required('Confirm password is required')
        .oneOf([Yup.ref('password'), null], 'Passwords must match'),
    }),
    onSubmit: (values, { setSubmitting }) => {
      //setIsLoading(true); // show loading spinner
      if (isSubmitting) {
        handleDisabledSubmit();
        return;
      }
      console.log(setSubmitting);
  axios
    .post('https://fullstack-test-navy.vercel.app/api/users/create', {
      full_name: values.fullName,
      contact_number: values.contactNumber,
      email: values.email,
      date_of_birth: `${values.day}-${values.month}-${values.year}`,
      password: values.password,
      confirm_password: values.confirmPassword,
    })
    .then((response) => {
      // setIsLoading(false); // hide loading spinner
      console.log(setSubmitting);
      setAlertVariant('success'); // set alert color to green
      setAlertMessage(response.data.title); // set alert message
      toast.success("User account successfully created.", {
        position: "top-right",
      className: 'success',
      hideProgressBar: true
      });

    })
    .catch((error) => {
      setIsLoading(false); // hide loading spinner
      setAlertVariant('danger'); // set alert color to red
      setAlertMessage(error.response.data.title); // set alert message
      toast.error("There was an error creating the account.", {
        position: "top-right",
        className: 'toast-message',
        hideProgressBar: true
     });
    })
    .finally(() => {
      setSubmitting(false); // enable submit button
    });
},
  });


  const [isLoading, setIsLoading] = useState(false);
  const [alertVariant, setAlertVariant] = useState('');
  const [alertMessage, setAlertMessage] = useState('');

  const {
    values,
    touched,
    errors,
    isSubmitting,
    handleChange,
    handleBlur,
    handleSubmit,
  } = formik;




  return (
    <div>
      <Header />
      <ToastContainer />
      <div className="form-header">
        <h3>Create User Account</h3>
      </div>
      <div className='RegistrationForm'>
        <Form noValidate onSubmit={handleSubmit}>
          <Form.Group controlId='fullName'>
            <Form.Label>Full Name</Form.Label>
            <Form.Control
              type='text'
              placeholder='Enter full name'
              name='fullName'
              value={values.fullName}
              onChange={handleChange}
              onBlur={handleBlur}
              isInvalid={touched.fullName && !!errors.fullName}
            />
            <Form.Control.Feedback type='invalid'>
              {errors.fullName}
            </Form.Control.Feedback>
            </Form.Group>
            <Form.Group controlId='email'>
  <Form.Label>Email</Form.Label>
  <Form.Control
    type='email'
    placeholder='Enter email'
    name='email'
    value={values.email}
    onChange={handleChange}
    onBlur={handleBlur}
    isInvalid={touched.email && !!errors.email}
  />
  <Form.Control.Feedback type='invalid'>
    {errors.email}
  </Form.Control.Feedback>
</Form.Group>

<Form.Group controlId='contactNumber'>
  <Form.Label>Contact Number</Form.Label>
  <InputGroup>
    <InputGroup.Text id='inputGroupPrepend'>+1</InputGroup.Text>
    <Form.Control
      type='tel'
      placeholder='555-555-5555'
      name='contactNumber'
      value={values.contactNumber}
      onChange={handleChange}
      onBlur={handleBlur}
      isInvalid={touched.contactNumber && !!errors.contactNumber}
    />
    <Form.Control.Feedback type='invalid'>
      {errors.contactNumber}
    </Form.Control.Feedback>
  </InputGroup>
</Form.Group>

<Form.Group controlId='dateOfBirth'>
  <Form.Label>Date of Birth</Form.Label>
  <div className='row'>
    <div className='col'>
      <Form.Control
        as='select'
        name='day'
        value={values.day}
        onChange={handleChange}
        onBlur={handleBlur}
        isInvalid={touched.day && !!errors.day}
      >
        <option value='' disabled hidden>
          Day
        </option>
        {days.map((day) => (
          <option key={day} value={day}>
            {day}
          </option>
        ))}
      </Form.Control>
      <Form.Control.Feedback type='invalid'>
        {errors.day}
      </Form.Control.Feedback>
    </div>
    <div className='col'>
      <Form.Control
        as='select'
        name='month'
        value={values.month}
        onChange={handleChange}
        onBlur={handleBlur}
        isInvalid={touched.month && !!errors.month}
      >
        <option value='' disabled hidden>
          Month
        </option>
        {months.map((month, index) => (
          <option key={index} value={month}>
            {month}
          </option>
        ))}
      </Form.Control>
      <Form.Control.Feedback type='invalid'>
        {errors.month}
      </Form.Control.Feedback>
    </div>
    <div className='col'>
      <Form.Control
        as='select'
        name='year'
        value={values.year}
        onChange={handleChange}
        onBlur={handleBlur}
        isInvalid={touched.year && !!errors.year}
      >
        <option value='' disabled hidden>
          Year
        </option>
        {years.map((year) => (
          <option key={year} value={year}>
            {year}
          </option>
        ))}
      </Form.Control>
      <Form.Control.Feedback type='invalid'>
        {errors.year}
      </Form.Control.Feedback>
    </div>
  </div>
</Form.Group>

<Form.Group controlId='password'>
  <Form.Label>Password</Form.Label>
  <Form.Control
    type='password'
    placeholder='Enter password'
    name='password'
    value={values.password}
    onChange={handleChange}
    onBlur={handleBlur}
    isInvalid={touched.password && !!errors.password}
  />
  <Form.Control.Feedback type='invalid'>
    {errors.password}
  </Form.Control.Feedback>
</Form.Group>
<Form.Group controlId='confirmPassword'>
  <Form.Label>Confirm Password</Form.Label>
  <Form.Control
    type='password'
    placeholder='Confirm password'
    name='confirmPassword'
    value={values.confirmPassword}
    onChange={handleChange}
    onBlur={handleBlur}
    isInvalid={touched.confirmPassword && !!errors.confirmPassword}
  />
  <Form.Control.Feedback type='invalid'>
    {errors.confirmPassword}
  </Form.Control.Feedback>
</Form.Group>


{isLoading && <Spinner animation='border' role='status' />}
</Form>
</div>
<div className="button">
  <Button variant='primary' type='submit' disabled={isSubmitting} onClick={isSubmitting ? handleDisabledSubmit : handleSubmit}>
    Create Account
  </Button>
</div>
<Alert variant={alertVariant} message={alertMessage} />
</div>
);
};

export default RegistrationForm;
